mapper.select:
SELECT wn.*,
       wu.name AS authorName
FROM wm_news wn
         LEFT JOIN wm_user wu ON wn.user_id = wu.id


        AND wn.title LIKE #{dto.title}



            AND wn.status = #{dto.status}


            AND wn.status = 0



LIMIT #{dto.page}, #{dto.size}

SELECT count(*)
FROM wm_news wn
         LEFT JOIN wm_user wu ON wn.user_id = wu.id


        and wn.title like #{dto.title}



            and wn.status = #{dto.status}


            and wn.status != 0
